DROP TABLE IF EXISTS `paper`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `paper` (
  `id_paper` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `citation` varchar(100) NOT NULL,
  `bib` mediumtext,
  `referenceCount` int(20) DEFAULT '0',
  `author` varchar(500) DEFAULT '',
  `keywords` varchar(500) DEFAULT '',
  `id_taxonomy` int(11) unsigned NOT NULL DEFAULT '1',
  PRIMARY KEY (`id_taxonomy`,`id_paper`),
  UNIQUE KEY `paper_id_UNIQUE` (`id_taxonomy`,`id_paper`),
  UNIQUE KEY `paper_citation_UNIQUE` (`id_taxonomy`,`citation`),
  KEY `paper_id_paper` (`id_paper`),
  CONSTRAINT `paper_id_taxonomy_foreign` FOREIGN KEY (`id_taxonomy`) REFERENCES `taxonomy` (`id_taxonomy`) ON DELETE CASCADE ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=50 DEFAULT CHARSET=utf8;
